import React, { Component } from "react";
import AppBar from "material-ui/AppBar";
import RaisedButton from "material-ui/RaisedButton";
import TextField from "material-ui/TextField";
import store from "../../stores/store.js";
//import theme from "./theme";
import lightBaseTheme from "material-ui/styles/baseThemes/darkBaseTheme";
import { deepOrangeA700 } from "material-ui/styles/colors";
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import getMuiTheme from "material-ui/styles/getMuiTheme";
import * as Colors from "material-ui/styles/colors";
import { fade } from "material-ui/utils/colorManipulator";

const muiTheme = getMuiTheme({
  appBar: {
    color: Colors.cyan400,
    textColor: Colors.white
  },
  palette: {
    primary1Color: Colors.cyan500,
    primary2Color: Colors.blueGrey700,
    accent1Color: Colors.deepOrange500,
    accent2Color: Colors.blueGrey400,
    accent3Color: Colors.blueGrey500,
    height: 80
  },
  cardMedia: {
    overlayContentBackground: fade(Colors.darkBlack, 0.87)
  }
});

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "admin2",
      password: "admin2",
      isAuthenticated: false
    };
    this.onSubmit = this.onSubmit.bind(this);
  }

  onSubmit(e) {
    e.preventDefault();
    onLogin(this.state.username, this.state.password);
    this.props.history.push("/dashboard");
  }

  onUserNameChange(e) {
    this.setState({ username: e.target.value });
  }

  onPasswordChange(e) {
    this.setState({ password: e.target.value });
  }

  handleClick(event) {
    // var apiBaseUrl = "http://localhost:4000/api/";
    console.log("Login::Dispatching event for user login", this.state.username);
    store.dispatch({
      type: "user_login",
      userId: this.state.username,
      password: this.state.password
    });
    this.props.history.push("/dashboard");
  }

  render() {
    return (
      <div>
        <MuiThemeProvider muiTheme={getMuiTheme(muiTheme)}>
          <div>
            <AppBar title="NMS Log Manager" />
            <TextField
              hintText="Enter your Username"
              floatingLabelText="Username"
              onChange={(event, newValue) =>
                this.setState({ username: newValue })
              }
            />
            <br />
            <TextField
              type="password"
              hintText="Enter your Password"
              floatingLabelText="Password"
              onChange={(event, newValue) =>
                this.setState({ password: newValue })
              }
            />
            <br />
            <RaisedButton
              label="Submit"
              primary={true}
              style={style}
              onClick={event => this.handleClick(event)}
            />
          </div>
        </MuiThemeProvider>
      </div>
    );
  }
}
const style = {
  margin: 25
};
export default Login;
